public class MusicContentCenterConfiguration {
  public String appId;
  public String rtmToken;
  public long mccUid;
  public IMusicContentCenterEventHandler eventHandler;

  public MusicContentCenterConfiguration() {
    this.appId = null;
    this.rtmToken = null;
    this.mccUid = 0;
    eventHandler = null;
  }

  public MusicContentCenterConfiguration(
      String appid, String token, long mccUid, IMusicContentCenterEventHandler eventHandler) {
    this.appId = appid;
    this.rtmToken = token;
    this.mccUid = mccUid;
    this.eventHandler = eventHandler;
  }

  @CalledByNative
  public String getAppId() {
    return this.appId;
  }

  @CalledByNative
  public String getRtmToken() {
    return this.rtmToken;
  }

  @CalledByNative
  public long getMccUid() {
    return this.mccUid;
  }

  @CalledByNative
  public IMusicContentCenterEventHandler getEventHandler() {
    return eventHandler;
  }
}

__attribute__((visibility("default"))) @interface AgoraMusicContentCenterConfig : NSObject
@property(assign, nonatomic) AgoraRtcEngineKit* _Nullable rtcEngine;
@property (nonatomic, copy) NSString *appId;
@property (nonatomic, copy) NSString *rtmToken;
@property (nonatomic, assign) NSInteger mccUid;
@end

struct MusicContentCenterConfiguration {
    const char *appId;
    const char *rtmToken;
    int64_t mccUid;
    IMusicContentCenterEventHandler* eventHandler;
    MusicContentCenterConfiguration():appId(nullptr),rtmToken(nullptr),eventHandler(nullptr),mccUid(0){}
    MusicContentCenterConfiguration(const char*appid,const char* token,int64_t id,IMusicContentCenterEventHandler* handler):
        appId(appid),rtmToken(token),mccUid(id),eventHandler(handler){}
};
