static public class CaptureFormat {
      public int width;
      public int height;
      public int fps;
      }

struct VideoFormat {
                int width;
                int height;
                int fps;
              }

public class VideoFormat
    {
        public enum OPTIONAL_ENUM_SIZE_T
        {
            /** The maximum value (px) of the width. */
            kMaxWidthInPixels = 3840,
            /** The maximum value (px) of the height. */
            kMaxHeightInPixels = 2160,
            /** The maximum value (fps) of the frame rate. */
            kMaxFps = 60,
        }

        public VideoFormat()
        {
            width = (int)FRAME_WIDTH.FRAME_WIDTH_640;
            height = (int)FRAME_HEIGHT.FRAME_HEIGHT_360;
            fps = (int)FRAME_RATE.FRAME_RATE_FPS_15;
        }

        public VideoFormat(int w, int h, int f)
        {
            this.width = w;
            this.height = h;
            this.fps = f;
        }
        public int width { set; get; }
        public int height { set; get; }
        public int fps { set; get; }
    };

class VideoFormat {
  const VideoFormat({this.width, this.height, this.fps});

  @JsonKey(name: 'width')
  final int? width;
  @JsonKey(name: 'height')
  final int? height;
  @JsonKey(name: 'fps')
  final int? fps;
  factory VideoFormat.fromJson(Map<String, dynamic> json) =>
      _$VideoFormatFromJson(json);
  Map<String, dynamic> toJson() => _$VideoFormatToJson(this);
}
