public class CameraCapturerConfiguration {
  public enum CAMERA_DIRECTION {
    CAMERA_REAR(0),
    CAMERA_FRONT(1);
  }
  public CAMERA_DIRECTION cameraDirection;
  static public class CaptureFormat {
    public int width;
    public int height;
    public int fps;
  }
  public CaptureFormat captureFormat;
}

