public class SegmentationProperty {
  public static final int SEG_MODEL_AGORA_AI_ONE = 0;
  public static final int SEG_MODEL_AGORA_GREEN = 2;
  public int modeltype;
  public int prefervelocity;
  public float greencapacity;
  public SegmentationProperty(int modeltype, int prefervelocity, float greencapacity) {
    this.modeltype = modeltype;
    this.prefervelocity = prefervelocity;
    this.greencapacity = greencapacity;
  }

  public SegmentationProperty() {
    this.modeltype = SEG_MODEL_AGORA_GREEN;
    this.prefervelocity = 1;
    this.greencapacity = 0.5f;
  }
}

__attribute__((visibility("default"))) @interface AgoraSegmentationProperty: NSObject
@property(nonatomic, assign) SegModelType modeltype;
@property(nonatomic, assign) float greencapacity;
@property(nonatomic, assign) NSUInteger prefervelocity;
@end

struct SegmentationProperty {
    enum SEG_MODEL_TYPE {
    SEG_MODEL_AGORA_AI_ONE = 0, 
    SEG_MODEL_AGORA_GREEN = 2
  };
  SEG_MODEL_TYPE modeltype;
  int prefervelocity;
  float greencapacity;
  SegmentationProperty() : modeltype(SEG_MODEL_AGORA_AI_ONE), prefervelocity(1), greencapacity(0.5){}
};
