struct UserInfo {
                    uid_t uid;
                    char userAccount[MAX_USER_ACCOUNT_LENGTH];
                    UserInfo()
                    : uid(0) {
                    userAccount[0] = '\0';
                    }
                    };

public class UserInfo {
  public int uid;
  public String userAccount;

  @CalledByNative
  public UserInfo(int uid, String userAccount) {
    this.uid = uid;
    this.userAccount = userAccount;
  }
}

