public class GameContext {
    @NonNull
    private Context context;                                
    @NonNull
    private final String  appId;                            
    @NonNull
    private final String uid;                               
    @NonNull
    private final String rtmToken;                          
    @NonNull
    private final LogConfig logConfig;                      
    @Nullable
    public final IGameEngineEventHandler  eventHandler;     
}

@interface AGEGameContext : NSObject
@property (nonatomic, copy) NSString *appId;                           
@property (nonatomic, copy) NSString *uid;                              
@property (nonatomic, copy) NSString *rtmToken;                         
@property (nonatomic, weak) id<AGEGameEngineEventProtocol> delegate;  
@property (nonatomic, strong) AGELogConfig *logconfig;                  
@end
