public class LiveInjectStreamConfig {

    public enum AudioSampleRateType {
        TYPE_32000(32000),
        TYPE_44100(44100),
        TYPE_48000(48000);
    }
    public int width;
    public int height;
    public int videoGop;
    public int videoFramerate;
    public int videoBitrate;
    public AudioSampleRateType audioSampleRate;
    public int audioBitrate;
    public int audioChannels;

    public LiveInjectStreamConfig() {
        width = 0;
        height = 0;
        videoGop = 30;
        videoFramerate = 15;
        videoBitrate = 400;
        audioSampleRate = AudioSampleRateType.TYPE_44100;
        audioBitrate = 48;
        audioChannels = 1;
    }
}

__attribute__((visibility("default"))) @interface AgoraLiveInjectStreamConfig : NSObject

@property(assign, nonatomic) CGSize size;
@property(assign, nonatomic) NSInteger videoGop;
@property(assign, nonatomic) NSInteger videoFramerate;
@property(assign, nonatomic) NSInteger videoBitrate;
@property(assign, nonatomic) AgoraAudioSampleRateType audioSampleRate;
@property(assign, nonatomic) NSInteger audioBitrate;
@property(assign, nonatomic) NSInteger audioChannels;
+ (AgoraLiveInjectStreamConfig *_Nonnull)defaultConfig;
@end

struct InjectStreamConfig {
    int width;
    int height;
    int videoGop;
    int videoFramerate;
    int videoBitrate;
    AUDIO_SAMPLE_RATE_TYPE audioSampleRate;
    int audioBitrate;
    int audioChannels;

    InjectStreamConfig()
        : width(0)
        , height(0)
        , videoGop(30)
        , videoFramerate(15)
        , videoBitrate(400)
        , audioSampleRate(AUDIO_SAMPLE_RATE_48000)
        , audioBitrate(48)
        , audioChannels(1)
    {}
    };
