public class AgoraRhythmPlayerConfig {
  public int beatsPerMeasure;
  public int beatsPerMinute;

  public AgoraRhythmPlayerConfig() {
    this.beatsPerMeasure = 4;
    this.beatsPerMinute = 60;
  }

  @CalledByNative
  public int getBeatsPerMeasure() {
    return beatsPerMeasure;
  }

  @CalledByNative
  public int getBeatsPerMinute() {
    return beatsPerMinute;
  }
}

__attribute__((visibility("default"))) @interface AgoraRhythmPlayerConfig: NSObject
@property (assign, nonatomic) int beatsPerMeasure;
@property (assign, nonatomic) int beatsPerMinute;

@end

struct AgoraRhythmPlayerConfig {
  int beatsPerMeasure;
  int beatsPerMinute;

  AgoraRhythmPlayerConfig() : beatsPerMeasure(4), beatsPerMinute(60) {}
};

public class AgoraRhythmPlayerConfig  {

    public AgoraRhythmPlayerConfig() {
    beatsPerMeasure = 4;
    beatsPerMinute = 60;
    publish = true;
    }
    public int beatsPerMeasure;
    public int beatsPerMinute;
    public boolean publish;
    }

