struct RemoteVideoStats {
    uid_t uid;
    int delay;
    int width;
    int height;
    int receivedBitrate;
    int decoderOutputFrameRate;
    int rendererOutputFrameRate;
    int packetLossRate;
    REMOTE_VIDEO_STREAM_TYPE rxStreamType;
    int totalFrozenTime;
    int frozenRate;
    int totalActiveTime;
    int publishDuration;
    };

